mapper.select:
SELECT * , course_type.course_type_name
    FROM Course
    JOIN course_type ON course.course_type_id = course_type.id;

mapper.update:
update Course
course_name=#{course_name}, credit=#{credit}, num=#{num}, course_type_id=#{course_type_id}, pre_course_id=#{pre_course_id}, book=#{book}, extracurricular=#{extracurricular}
where id = #{id};

mapper.select:
select * from Course


        and course_name like #{course_name}


        and course_type_id = #{course_type_id}


limit #{pageNum} * 2,2;
